const display=document.querySelector("#display"),buttons=document.querySelectorAll("button");buttons.forEach((item=>{item.onclick=()=>{if("clear"==item.id)display.innerText="";else if("backspace"==item.id){let e=display.innerText.toString();display.innerText=e.substr(0,e.length-1)}else""!=display.innerText&&"equal"==item.id?display.innerText=eval(display.innerText):""==display.innerText&&"equal"==item.id?(display.innerText="Empty!",setTimeout((()=>display.innerText=""),2e3)):display.innerText+=item.id}}));const themeToggleBtn=document.querySelector(".theme-toggler"),calculator=document.querySelector(".calculator"),toggleIcon=document.querySelector(".toggler-icon");let isDark=!0;themeToggleBtn.onclick=()=>{calculator.classList.toggle("dark"),themeToggleBtn.classList.toggle("active"),isDark=!isDark};